mapper.select:
select ifnull(sum(d.delivery_fee),0) from delivery d inner join shop_info si on d.shop_idx=si.shop_idx inner join order_status os on d.order_idx=os.order_idx where rider_idx=#{rider_idx} and delivery_status='배달완료'
and date_format(pay_regdate,'%Y-%m-%d') between #{startDate} and #{endDate}
order by d.pay_regdate

select d.*, si.shop_name,si.shop_addr1,si.shop_addr2,os.mem_addr1,os.mem_addr2 from delivery d inner join shop_info si on d.shop_idx=si.shop_idx inner join order_status os on d.order_idx=os.order_idx where rider_idx=#{rider_idx} and delivery_status='배달완료'
and date_format(pay_regdate,'%Y-%m-%d') between #{startDate} and #{endDate}
order by d.pay_regdate
