mapper.insert:
INSERT INTO order_status ( order_status_idx, shop_idx, menu_idx, mem_idx, pay_idx, delivery_idx, mcuraddr_idx, order_idx, order_regdate, order_status, order_reason, mem_phone, order_request, rider_request, rider_status, amount, pay_type, coupon_use, mem_addr1, mem_addr2, menu_price, menu_cnt, menu_name
) VALUES
( null, #{item.shop_idx}, #{item.menu_idx}, #{item.mem_idx}, #{item.pay_idx}, null, #{item.mcuraddr_idx}, #{item.order_idx,}, NOW(), 'NONE', null, #{item.mem_phone}, #{item.order_request}, #{item.rider_request}, 'NONE', #{item.amount}, #{item.pay_type}, null, #{item.mem_addr1}, #{item.mem_addr2}, #{item.menu_price}, #{item.menu_cnt}, #{item.menu_name}
)

mapper.select:
SELECT COUNT(*)
FROM order_status_view
WHERE order_idx = #{order_idx}

select * from order_status_view 
where shop_idx = #{shop_idx} and order_status='배달완료' AND DATE(order_regdate) BETWEEN #{start_date} and #{end_date}
ORDER BY order_idx DESC

SELECT IFNULL(SUM(amount), 0) AS total_amount
FROM order_status_view
WHERE shop_idx = #{shop_idx}
  AND order_status = '배달완료'
  AND DATE(order_regdate) BETWEEN #{start_date} and #{end_date}

SELECT mem_addr1
FROM order_status_view
WHERE order_idx = #{order_idx}
